import React, { useState } from "react";
import { Form, Label, Input, Button, Toast, Alert, Row,Col } from "reactstrap";
import { send } from "emailjs-com";

export const Contact = () => {
  const [toSend, setToSend] = useState({
    from_name: "",
    email_id: "",
    message: "",
  });

  function Example() {
    const [show, setShow] = useState(false);

    return (
      <Row>
        <Col xs={12}>
          <Toast onClose={() => setShow(false)} show={show} delay={3000} autohide>
            <Toast.Header>
              <img
                src="holder.js/20x20?text=%20"
                className="rounded me-2"
                alt=""
              />
              <strong className="me-auto">Bootstrap</strong>
              <small>11 mins ago</small>
            </Toast.Header>
            <Toast.Body>Woohoo, you're reading this text in a Toast!</Toast.Body>
          </Toast>
        </Col>
        <Button onClick={() => setShow(true)}>Show Toast</Button>
      </Row>
    );
  }

  const onSubmit = (e) => {
    e.preventDefault();
    send(
      "service_dq8ta89",
      "template_a39ei36",
      toSend,
      "user_kkujkVfgHBuo6QJ8kXvmt"
    )
      .then((response) => {
        console.log("SUCCESS!", response.status, response.text);
      })
      .catch((err) => {
        console.log("FAILED...", err);
      });

    setToSend({ from_name: "", email_id: "", message: "" });
  };

  const handleChange = (e) => {
    setToSend({ ...toSend, [e.target.name]: e.target.value });
  };

  return (
    <>
      <div className="contentSection contactDiv">
        <div className="Card mb-3 mt-3">
          <div className="Row no-gutters">
            <div className="Col-lg-12">
              <div className="CardBody">
                <div className="Card mb-3 CardContent">
                  <div className="Row no-gutters">
                    <div className="Col-lg-12">
                      <div className="CardBody">
                        <h4 className="Card-title" id="contactTitle">
                          LEAVE A MESSAGE -{" "}
                        </h4>{" "}
                        <br />
                        <div className="Card-text">
                          <Form id="contact-form" onSubmit={onSubmit}>
                            <Label for="name" className="contact-label">
                              Name :
                            </Label>
                            <Input
                              name="from_name"
                              type="text"
                              placeholder="Enter your Name."
                              className="contact-form form-control-lg"
                              autocomplete="off"
                              value={toSend.from_name}
                              onChange={handleChange}
                            />{" "}
                            <br />
                            <Label for="email" className="contact-label">
                              Email id :
                            </Label>
                            <Input
                              id="email"
                              type="email"
                              placeholder="Enter your email id."
                              className="contact-form form-control-lg"
                              name="email_id"
                              autocomplete="off"
                              value={toSend.email_id}
                              onChange={handleChange}
                            />{" "}
                            <br />
                            <Label for="message" className="contact-label">
                              Message :
                            </Label>
                            <textarea
                              id="message"
                              name="message"
                              Cols="60"
                              Rows="6"
                              placeholder="Type a message "
                              className="contact-form"
                              value={toSend.message}
                              onChange={handleChange}
                            ></textarea>
                            <Button
                              type="submit"
                              className="btn btn-lg"
                              id="sendBtn"
                            >
                              Send Message
                            </Button>
                          </Form>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          render(<Example />);
        </div>
      </div>

      <div className="contentSection contactDiv">
        <div className="Card mb-3 card-content">
          <div className="row no-gutters">
            <div className="col-lg-12">
              <div className="card-body">
                <h4 className="card-title" id="contactTitle">
                  OR REACH ME AT -{" "}
                </h4>
                <div className="card-text" style={{ textAlign: "center" }}>
                  <a href="tel:+91-7276072667" className="Link">
                    <i
                      className="fa fa-phone-square fa-2x"
                      title="+91 7276072667"
                    ></i>
                  </a>{" "}
                  <span> &nbsp; </span>
                  <a href="mailto:maithiaharsh@gmail.com" className="Link">
                    <i
                      className="fa fa-envelope fa-2x"
                      title="maithiaharsh@gmail.com"
                    ></i>
                  </a>{" "}
                  <span> &nbsp; </span>
                  <a href="#" className="Link">
                    <i className="fa fa-linkedin fa-2x" title="linkdin"></i>
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
